class TQueue {
public:
    TQueue()
        : N_{0}
        , Cnt_{}
    {
    }
    void Push(unsigned x) {
        Add(x, +1);
    }
    void Pop(unsigned x) {
        Add(x, -1);
    }
    unsigned And() const {
        unsigned ans = 0;
        for (int i = 0; i < 32; ++i) {
            if (Cnt_[i] == N_) {
                ans |= (1u << i);
            }
        }
        return ans;
    }
private:
    void Add(unsigned x, int d) {
        for (int i = 0; i < 32; ++i) {
            if (x & 1) {
                Cnt_[i] += d;
            }
            x >>= 1;
        }
        N_ += d;
    }
private:
    int N_;
    int Cnt_[32];
};


class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        TQueue q;
        int ans = numeric_limits<int>::max();
        int j = 0;

        for (int i = 0; i < n; ++i) {
            q.Push(nums[i]);

            while (true) {
                int cur = q.And();
                ans = min(ans, abs(cur - k));
                if (j >= i || cur > k) {
                    break;
                }
                q.Pop(nums[j++]);
            }
        }
        return ans;
    }
};
