class Solution {
    public int minimumDifference(int[] nums, int k) {
        Set<Integer> ands = new HashSet<>();
        ands.add(nums[nums.length - 1]);

        int minDiff = Math.abs(k - nums[nums.length - 1]);

        for (int i = nums.length - 2; i >= 0; i--) {
            Set<Integer> next = new HashSet<>();
            for (int a : ands) {
                int and = a & nums[i];
                next.add(and);
                minDiff = Math.min(minDiff, Math.abs(k - and));
            }
            next.add(nums[i]);
            minDiff = Math.min(minDiff, Math.abs(k - nums[i]));
            ands = next;
        }

        return minDiff;
    }
}
