class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        int n = a.size();

        vector<int> cur, tmp;
        int mi = 0x3f3f3f3f;
        for (int i : a) {
            cur.push_back(i);
            tmp.clear();
            for (int j : cur) {
                if (tmp.empty() || tmp.back() != (j & i)) {
                    tmp.push_back((j & i));
                }
            }

            cur = tmp;

            for (int j : cur) {
                mi = min(mi, abs(k - j));
            }
        }
        return mi;
    }
};
