class Solution {
public:
    int query(int l, int r, vector<vector<int>> &bit) {
        int ans = 0;
        for(int i = 0; i < 31; i++) {
            int x = bit[r][i];
            if(l) x -= bit[l-1][i];
            if(x==(r-l+1)) ans += (1<<i);
        }
        return ans;
    }

    int minimumDifference(vector<int>& nums, int k) {
        vector<vector<int>> bit(nums.size(),vector<int>(31,0));
        for(int i = 0; i < nums.size(); i++) {
            for(int j = 0; j < 31; j++) {
                if(i) bit[i][j] += bit[i-1][j];
                if((1<<j)&nums[i]) bit[i][j]++;
            }
        }
        int j = 0, ans = 1e9;
        for(int i = 0; i < nums.size(); i++)  {
            int val = query(j,i,bit);
            ans = min(ans,abs(k-val));
            while(j<i&&(val<k)) {
                j++;
                val = query(j,i,bit);
                ans = min(ans,abs(k-val));
            }
        }
        return ans;
    }
};
