class Solution {
public:
    int pre[100010][40];
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size(),minn=abs(nums[0]-k);
        for(int i=0;i<n;i++){
            int oo=nums[i];
            for(int j=0;j<35;j++){
                pre[i+1][j]=pre[i][j];
                if(oo%2) pre[i+1][j]++;
                oo/=2;
            }
        }
        int l=1,res=nums[0];
        for(int i=2;i<=n;i++){
            res&=nums[i-1];
            minn=min(minn,abs(res-k));
            while(l+1<=i&&res<k){
                l++;
                long long p=1;
                res=0;
                for(int j=0;j<35;j++){
                    if(pre[i][j]-pre[l-1][j]==i-l+1){
                        res+=p;
                    }
                    p*=2;
                }
                minn=min(minn,abs(res-k));
            }
        }
        return minn;
    }
};
