class Solution {
public:
    class SegTree {
    public:
        vector<int> tree;
        int size;

        SegTree(const vector<int>& nums) {
            size = nums.size();
            tree.resize(4 * size);
            build(nums, 0, 0, size - 1);
        }

        void build(const vector<int>& nums, int node, int start, int end) {
            if (start == end) {
                tree[node] = nums[start];
            } else {
                int mid = (start + end) / 2;
                build(nums, 2 * node + 1, start, mid);
                build(nums, 2 * node + 2, mid + 1, end);
                tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
            }
        }

        int query(int l, int r, int node, int start, int end) {
            if (r < start || l > end) {
                return INT_MAX; // All bits set
            }
            if (l <= start && end <= r) {
                return tree[node];
            }
            int mid = (start + end) / 2;
            int left = query(l, r, 2 * node + 1, start, mid);
            int right = query(l, r, 2 * node + 2, mid + 1, end);
            return left & right;
        }

        int query(int l, int r) {
            return query(l, r, 0, 0, size - 1);
        }
    };

    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        SegTree segT(nums);
        int ans = INT_MAX;

        for (int i = 0; i < n; ++i) {
            int l = i, r = n - 1, best = -1;
            while (l <= r) {
                int mid = l + (r - l) / 2;
                int andVal = segT.query(i, mid);
                if (abs(k - andVal) < ans) {
                    ans = abs(k - andVal);
                    best = mid;
                }
                if (andVal >= k) 
                    l = mid + 1;
                 else 
                    r = mid - 1;

            }
        }

        return ans;
    }
};
