class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> tp(n,vector<int>(32));
        int ans=1e9;
        for(int i=0;i<n;i++){
            for(int j=0;j<32;j++){
                if((nums[i]>>j)&1){
                    tp[i][j]++;
                }
                if(i>0){
                    tp[i][j]+=tp[i-1][j];
                }
            }
            int l=0,h=i;
            while(l<=h){
                int mid=(l+h)/2;;
                int crv=0;
                for(int j=0;j<32;j++){
                    int pf=mid==0?0:tp[mid-1][j];
                    if(tp[i][j]-pf==i-mid+1){
                        crv|=(1<<j);
                    }
                }
                ans=min(ans,abs(crv-k));
                if(crv>k){
                    h=mid-1;
                }
                else{
                    l=mid+1;
                }
            }
        }
        return ans;
    }
};
