class Solution {
    public int minimumDifference(int[] nums, int k) {
        var ts = new TreeSet<Integer>();
        int n = nums.length;
        int res = Integer.MAX_VALUE;
        for(int i = 0; i < n; i++){
            var next = new TreeSet<Integer>();
            for(int t : ts){
                next.add(nums[i] & t);
            }
            next.add(nums[i]);
            Integer ceil = next.ceiling(k);
            if(ceil != null)
                res = Math.min(res, ceil - k);
            Integer floor = next.floor(k);
            if(floor != null)
                res = Math.min(res, k - floor);
            ts = next;
        }
        return res;
    }
}
