class Solution {
public:
    int l2(int i) {
        return i ? __builtin_clzll(1) - __builtin_clzll(i) : -1;
    }

    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> st(20, vector<int>(n));
        st[0] = nums;
        for (int i = 1; i < 20; i++){
            for (int j = 0; j + (1 << i) <= n; j++) {
                st[i][j] = st[i - 1][j] & st[i - 1][j + (1 << (i - 1))];
            }
        }
        function<int(int, int)> qry = [&](int l, int r) {
            int i = l2(r - l + 1);
            return st[i][l] & st[i][r - (1 << i) + 1];
        };
        int ans = 2e9;
        for (int i = 0; i < n; i++){
            if (nums[i] <= k) {
                ans = min(ans, k - nums[i]);
            } else {
                int lo = i, hi = n - 1;
                int bst = -1;
                while (lo <= hi) {
                    int m = (lo + hi) / 2;
                    if (qry(i, m) >= k) {
                        lo = m + 1;
                        ans = min(ans, qry(i, m) - k);
                    } else {
                        hi = m - 1;
                    }
                }
                lo = i, hi = n - 1;
                while (lo <= hi) {
                    int m = (lo + hi) / 2;
                    if (qry(i, m) <= k) {
                        hi = m - 1;
                        ans = min(ans, k - qry(i, m));
                    } else {
                        lo = m + 1;
                    }
                }
            }
        }
        return ans;
    }
};
