class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        freq = [0]*32
        mask = (1<<32)-1 
        ii = 0
        ans = inf 
        for i, x in enumerate(nums): 
            for j in range(32): 
                if not x & 1<<j: 
                    if freq[j] == 0: mask ^= 1<<j
                    freq[j] += 1
            # print(i, x, mask)
            ans = min(ans, abs(mask-k))
            while ii < i and mask < k: 
                for j in range(32):
                    if not nums[ii] & 1<<j: 
                        freq[j] -= 1
                        if freq[j] == 0: mask ^= 1<<j
                ans = min(ans, abs(mask-k))
                ii += 1
        return ans
