class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
         int n = nums.size();
        map<int, int> mp;
        long  long  ans = 5e10;
        for (int i = 0; i < n; i++)
        {
            map<int, int> ndp;
            for (auto [x, y] : mp)
            {
                ndp[x & nums[i]] += y;
            }
            ndp[nums[i]]++;
            mp = ndp;
            for (auto [x, y] : mp)
            {
                ans = min(ans, 1LL*abs(k - x));
            }
        }
        return ans;
    }
};
