class Solution {
    public int minimumDifference(int[] nums, int k) {
        TreeSet<Integer> t = new TreeSet<>();
        int x0 = nums[0];
        for (int x : nums) {
            TreeSet<Integer> t2 = new TreeSet<>();
            for (int y : t)
                t2.add(y & x);
            t2.add(x);
            Integer p = t2.floor(k);
            if (p != null && Math.abs(p - k) < Math.abs(x0 - k))
                x0 = p;
            p = t2.ceiling(k);
            if (p != null && Math.abs(p - k) < Math.abs(x0 - k))
                x0 = p;
            t = t2;
        }
        return Math.abs(x0 - k);
    }
}
