template <typename T, class func = function<T(const T &, const T &)>>
class spashTable {
public:
  int n, Log;
  func myfunc;
  T **Table, *arr;
  spashTable(vector<T> & lst, const func & F) : myfunc(F) { 
    n = lst.size(), Log = ceil(log2((int)lst.size())) + 5;
    arr = new T[n + 1];
    Table = new T*[n + 1];
    for(int i = 0; i < n; i++) {
      Table[i] = new T[Log];
      arr[i] = lst[i], Table[i][0] = lst[i];
    }
    buildTable();
  }
	void buildTable() {
		for (int j = 1; j <= Log; j++) {
			for (int i = 0; (i + (1 << j) - 1) < n; i++)
				Table[i][j] = myfunc(Table[i][j - 1], Table[i + (1 << (j - 1))][j - 1]);
		}
	}
	T query(int l, int r){
		int j = __lg(r - l + 1);
		return myfunc(Table[l][j], Table[r - (1 << j) + 1][j]);
	}
};
class Solution {
public:
  int minimumDifference(vector<int>& nums, int k) {
    int result = INT32_MAX;
    spashTable<int> st(nums, [&](int a, int b) { return a & b; });
    for (int i = 0; i < nums.size(); i++) {
      if (nums[i] <= k) {
        result = min(result, abs(k - nums[i]));
      }
      int l = i, r = nums.size() - 1, ind = i;
      while (l <= r) {
        int mid = (l + r) >> 1;
        if (st.query(i, mid) >= k) {
          ind = mid, l = mid + 1;
        } else r = mid - 1;
      }
      result = min(result, abs(k - st.query(i, ind)));
      if (ind > i) result = min(result, abs(k - st.query(i, ind - 1)));
      if (ind < nums.size() - 1) result = min(result, abs(k - st.query(i, ind + 1)));
    }
    return result;
  }
};
