class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> v(n);
        int ans = abs(k - nums[0]);
        v[0].push_back(nums[0]);
        for(int i = 1, tmp; i < n; i++) {
            for(auto &j: v[i-1]) {
                tmp = j & nums[i];
                if(!v[i].empty() && v[i].back() == tmp) continue;
                v[i].push_back(tmp);
                ans = min(ans, abs(k - tmp));
            }
            v[i].push_back(nums[i]);
            ans = min(ans, abs(k - nums[i]));
        }
        return ans;
    }
};
