class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int l = 0, r = 0;
        unordered_map<int, int> bits;
        int mn = abs(k - nums[0]);
        while (r < nums.size()) {
            for (int j = 1; j <= nums[r]; j <<= 1) {
                if ((j & nums[r]) == j) {
                    bits[j]++;
                }
            }
            ++r;
            int total = 0;
            for (auto& [i, j] : bits) {
                if (j == r - l) {
                    total += i;
                }
            }
            mn = min(mn, abs(k - total));
            while (total < k && l < r) {
                for (int j = 1; j <= nums[l]; j <<= 1) {
                    if ((j & nums[l]) == j) {
                        bits[j]--;
                    }
                }
                ++l;
                total = 0;
                for (auto& [i, j] : bits) {
                    if (j == r - l && j != 0) {
                        total += i;
                    }
                }
                mn = min(mn, abs(k - total));
            }
        }
        return mn;
    }
};
