class Solution {
public:
    vector<int> solve(int x) 
    {
        vector<int> v;
        int cnt = 0;
        while (x > 0) 
        {
            if (x & 1) 
            v.push_back(cnt);

            x >>= 1;
            cnt++;
        }
        return v;
    }


    int minimumDifference(vector<int>& nums, int k) {
        if (nums.empty()) 
        return k;

        int temp = nums[0];
        int diff = abs(temp - k);

        unordered_map<int, int> mp;
        for (int x : solve(nums[0])) 
            mp[x]++;

        int n=nums.size();
        int i = 0, j = 0;

        while (j < n) 
        {
            if (temp > k || i > j) 
            {
                    j++;
                if (j >= n) 
                    break;

                for (int x : solve(nums[j])) 
                    mp[x]++;
            } 
            else 
            {
                for (auto x : solve(nums[i])) 
                {
                    mp[x]--;
                    if(mp[x]==0) 
                    mp.erase(x);
                }
                i++;
            }

            int len = j-i+1;
            int ans=0;
            for (auto it : mp) 
            {
                if (it.second == len) 
                ans |= (1 << it.first);
            }

            temp=ans;

            if (i <= j) 
            diff = min(diff, abs(temp - k));

        }

        return diff;
    }
};


