class Solution {
public:
    int prefix_count[31][100007];
    int minimumDifference(vector<int>& v, int k) {
        pre(v);
        int ans = 2e9 + 7;
        for(int i = 0; i < v.size(); i++) {
            int lo = 0, hi = i, ind = i;
            while(lo <= hi) {
                int m = (lo + hi) / 2;
                if(q(m, i) >= k) {
                    ind = m;
                    hi = m - 1;
                }
                else lo = m + 1;
            }
            // cout << ind << endl;
            // cout << q(ind, i)
            ans = min(ans, abs(k - q(ind, i)));
            if(ind > 0) ans = min(ans, abs(k - q(ind-1, i)));
        }
        return ans;
    }
    void pre(vector<int> arr)
    {
        int n = arr.size();
        for (int i = 0; i < 31; i++) {
            prefix_count[i][0] = ((arr[0] >> i) & 1);
            for (int j = 1; j < n; j++) {
                prefix_count[i][j] = ((arr[j] >> i) & 1);
                prefix_count[i][j] += prefix_count[i][j - 1];
            }
        }
    }

    int q(int l, int r)
    {
        int ans = 0;
        for (int i = 0; i < 31; i++) {
            int x;
            if (l == 0)
                x = prefix_count[i][r];
            else
                x = prefix_count[i][r]
                    - prefix_count[i][l - 1];

            if (x == r - l + 1)
                ans = (ans | (1 << i));
        }

        return ans;
    }
};
