class Solution {
public:
    void ft(vector<int> &bt,int x)
    {
      for(int i=0;i<32;i++)
      {
        if((1<<i)&x)
        bt[i]++;
      }
    }
    int ft2(vector<int> &bt,int xx,int x,int y)
    {
      int ans=0;
      for(int i=0;i<32;i++)
      {
        if((1<<i)&xx)
        bt[i]--;
        if(bt[i] == x-y)
        ans+=(1<<i);
      }
      return ans;
    }
    int f(vector<int> &nums,int k)
    {
      vector<int> bt(32,0);
      int x=-1;
      int ans=INT_MAX;
      int j=0;
      for(int i=0;i<nums.size();i++)
      {
        ft(bt,nums[i]);
        if(x == -1) x=nums[i];
        else x&=nums[i];
        ans=min(ans,abs(x-k));
        while(j <= i && x < k)
        {
           x=ft2(bt,nums[j],i,j);
           j++;
           ans=min(ans,abs(x-k));
        }
      }
      return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int mx=*max_element(nums.begin(),nums.end());
        if(k > mx) return k-mx;
        int a=f(nums,k);
        return a;
    }
};
