class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        j = -1
        u = [0]*32
        n = len(nums)
        ans = 1000000000000000
        for i in range(n):
            for b in range(32):
                if nums[i]&(1<<b): u[b] += 1
            r = 0
            for b in range(32):
                if u[b] == (i-j):
                    r += 1<<b
            ans = min(ans, abs(r-k))
            while r < k and j < i-1:
                r = 0
                j += 1
                for b in range(32):
                    if nums[j]&(1<<b): u[b] -= 1
                for b in range(32):
                    if u[b] == (i-j):
                        r += 1<<b
                ans = min(ans, abs(r-k))
        return ans
