class Solution {
public:
    vector<int> Bit(int x){
        vector<int> bit(31);
        int cnt = 0;
        while(cnt<=30){
            bit[cnt++]=(x%2);
            x/=2;
        }
        return bit;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int r = nums.size();
        int curr = INT_MAX;
        vector<int> t_bit(31);
        int l = 0,ans = INT_MAX;
        for(int r = 0;r<n;r++){
            vector<int> x = Bit(nums[r]);
            for(int j = 0;j<31;j++)
                if(!x[j])
                    t_bit[j]++;
            curr = (curr&nums[r]);
            ans = min(ans,abs(curr-k));
            int cnt = 1;
            while(curr<k){
                vector<int> x = Bit(nums[l++]);
                for(int j = 0;j<31;j++){
                    if(!x[j] && t_bit[j]==1)
                        curr+=(1<<j);
                    if(!x[j])
                        t_bit[j]--;
                }
                ans = min(ans,abs(curr-k));
            }
            ans = min(ans,abs(curr-k));
        }
        return ans;

    }
