class Solution {
public:
    int is(int s,int e,vector<vector<int>> &arr){
        int ans = 0;
        for(int i  = 0;i<30;i++){
            if( s!=0 && arr[e][i]==(arr[s-1][i]+(e-s+1))) ans+=(1<<i);
            else if(s==0 && arr[e][i]==e+1)ans+=(1<<i);
        }
        return ans;
    }
    int dev(int indx,int k,vector<vector<int>> &arr){
        int n = arr.size();
        int s = indx,e = n-1;
        int ans = 1e9;
        int temp = 1e9;
        while(s<=e){
            int mid = s+(e-s)/2;
            int val = is(indx,mid,arr);
            // cout<<val<<" "<<indx<<" "<<mid<<endl;;
            if(val>=k){
                temp = val-k;
                s = mid+1;
            }
            else  e = mid-1;
        }
        // cout<<temp<<endl;
        ans = min(ans,temp);
        s = indx,e = n-1;
        // int ans = 1e9;
        temp = 1e9;
        while(s<=e){
            int mid = s+(e-s)/2;
            int val = is(indx,mid,arr);
            // cout<<val<<" "<<indx<<" "<<mid<<endl;
            if(val<=k){
                temp = k-val;
                e = mid-1;
            }
            else  s = mid+1;
        }
        // cout<<temp<<endl;
        ans = min(ans,temp);
        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int ans = 1e9,n = nums.size();
        vector<vector<int>> arr(n,vector<int>(30,0));
        for(int i = 0;i<n;i++){
            for(int j = 0;j<30;j++){
                if(i!=0) arr[i][j]+=arr[i-1][j];
                if(nums[i] & (1<<j)) arr[i][j]++;
            }
        }
        for(int i = 0;i<n;i++){
            ans=min(ans,dev(i,k,arr));
        }
        return ans;
    }
};
