class Solution:
    def subarray_bitwise_and(self, arr):
        result_set = set()
        current_set = set()

        for num in arr:
            new_set = {num}
            for val in current_set:
                new_set.add(val & num)
            current_set = new_set
            result_set.update(current_set)

        return result_set

    def minimumDifference(self, nums: List[int], k: int) -> int:
        return min(abs(k - val) for val in self.subarray_bitwise_and(nums))
