class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        s = [set([nums[0]])]
        for it in range(1, len(nums)):
            cur_n = nums[it]
            new_s = set([cur_n])
            for el in s[-1]:
                new_s.add(el&cur_n)
            s.append(new_s)
        total = set()
        for el in s:
            total.update(el)
        ans = float('inf')
        for el in total:
            ans = min(ans, abs(el-k))
        return ans
