class Solution {
public:
    const int N =1e5+4;
    int st[400004];

    void build(int si,int ss,int se,vector<int>&arr){
        if(ss==se){
            st[si]=arr[ss-1];
        }
        else{
            int sm = (ss+se)>>1;
            build(2*si,ss,sm,arr);
            build(2*si+1,sm+1,se,arr);
            st[si]=st[2*si]&st[2*si+1];
        }
    }

    int query(int si,int ss,int se,int l,int r){
        if(ss>r  || se<l) return INT_MAX;
        if(ss>=l && se<=r) return st[si];
        int sm = (ss+se)>>1;
        return query(2*si,ss,sm,l,r) & query(2*si+1,sm+1,se,l,r);
    }



    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = 1e9;
        build(1,1,n,nums);
        for(int i=0;i<n;i++){
            int hi = n,lo=i+1;
            while(lo<=hi){
                int mid = lo + (hi-lo)/2;
                int qa = query(1,1,n,i+1,mid);
                if(qa<=k) ans = min(ans,k-qa),hi=mid-1;
                else lo=mid+1;
            }
            hi=n,lo=i+1;
            while(lo<=hi){
                int mid = lo+(hi-lo)/2;
                int qa = query(1,1,n,i+1,mid);
                if(qa>k){
                    ans = min(ans,qa-k);
                    lo=mid+1;
                }
                else hi = mid-1;
            }
        }
        return ans;
    }
};
