class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>v(n+1,vector<int>(33,0));
        for(int i=1;i<=n;i++){
            int x=nums[i-1];
            for(int j=0;j<=32;j++){
                long long p=1LL<<j;
                if((p&x)){
                    v[i][j]++;
                }
                v[i][j]+=v[i-1][j];
            }
        }
        long long ans=INT_MAX;
        for(int i=1;i<=n;i++){
            int x=nums[i-1];
            if(x<=k){
                ans=min(ans,1LL*abs(k-x));
            }
            else if(x>k){
                int lo=i,hi=n;
                int mx=i;
                while(lo<=hi){
                    int mid=lo+(hi-lo)/2;
                    long long p=0;
                    for(int j=0;j<=32;j++){
                        if(v[mid][j]-v[i-1][j]>=mid-(i-1)){
                            p+=1LL<<j;
                        }
                    }
                    //cout<<mid<<endl;
                    if(p>=k){
                        mx=max(mx,mid);
                        lo=mid+1;
                    }
                    else{
                        hi=mid-1;
                    }
                }
                long long p=0;
                for(int j=0;j<=32;j++){
                        if(v[mx][j]-v[i-1][j]>=mx-(i-1)){
                            p+=1LL<<j;
                        }
                }
                ans=min(ans,abs(p-k));
                if(mx+1<=n){
                    mx++;
                     p=0;
                    for(int j=0;j<=32;j++){
                            if(v[mx][j]-v[i-1][j]>=mx-(i-1)){
                                p+=1LL<<j;
                            }
                    }
                    ans=min(ans,abs(p-k));
                }

            }
        }
        return ans;


    }
};
