#define MOD 1000000007
#define INF 1e18
#define endl "\n"
#define pb push_back
#define ppb pop_back
#define ff first
#define ss second
#define sz(x) ((int)(x).size())
#define all(x) (x).begin(), (x).end()

class Solution {
public:
    int minimumDifference(vector<int>& v, int k) {
        int ans = INT_MAX;
        int n = sz(v);

        vector<vector<int>> pos(32);
        for(int i = 0; i < 32; i++) {
            for(int j = 0; j < n; j++) {
                if((1 << i) & v[j]) 
                    continue;
                pos[i].pb(j);
            }
        }

        for(int i = 0; i < n; i++) {
            int curr = INT_MAX;

            int index = i;
            while(index < n) {
                for(int j = 0; j < 32; j++) {
                    if((1 << j) & v[index])
                        continue;
                    if((1 << j) & curr)
                        curr ^= (1 << j);
                }

                int next = n;
                for(int j = 0; j < 32; j++) {
                    if((1 << j) & curr) {
                        auto it = upper_bound(all(pos[j]), index);
                        if(it != pos[j].end())
                            next = min(next, *it);
                    }
                }

                index = next;
                ans = min(ans, abs(curr - k));
            }
        }

        return ans;
    }
};
