class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        res = abs(nums[0] - k)
        valid = {nums[0]}
        for num in nums:
            valid = {x & num for x in valid} | {num}
            res = min(res, min(abs(x - k) for x in valid))
        return res
