class Solution {
public:
    int query(int l,int r,vector<vector<int>> &bits,vector<int> &nums)
    {
        if(l==r)
            return nums[l];
        l++;
        r++;


        int ans=0;
        for(int i=0;i<32;i++)
        {
            if(bits[r][i]-bits[l-1][i]==r-l+1)
                ans+=(1<<i);
        }
        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        int anss=1e9;
        vector<vector<int>> bits(n+1,vector<int>(32,0));
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<32;j++)
            {
                bits[i+1][j]=((nums[i]>>j)&1);
            }
        }
        for(int i=1;i<n;i++)
        {
            for(int j=0;j<32;j++)
            {
                 bits[i+1][j]+=bits[i][j];

            }


        }

        for(int i=0;i<n;i++)
        {
            int low=i+1,high=n-1;
            int ans=i;
            while(low<=high)
            {
                int mid=(low+high)/2;
                if(query(i,mid,bits,nums)>=k)
                {
                    ans=mid;
                    low=mid+1;
                }
                else high=mid-1;
            }
            // cout<<i<<" "<<ans<<" "<<query(i,ans,bits,nums)<<endl;
            anss=min(anss,abs(k-query(i,ans,bits,nums)));
            if(ans+1<n)
                anss=min(anss,abs(k-query(i,ans+1,bits,nums)));
            if(ans-1>=i)
                anss=min(anss,abs(k-query(i,ans-1,bits,nums)));

        }
        return anss;
    }
};
