class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int ans = INT_MAX;
        unordered_set<int> st;
        for(int i = 0; i < nums.size(); ++i){
            unordered_set<int> tmp;
            for(auto j : st){
                j &= nums[i];
                ans = min(ans, abs(j-k));
                if(j > k)
                    tmp.insert(j);
            }
            if(nums[i] > k)
                tmp.insert(nums[i]);
            ans = min(ans, abs(nums[i]-k));
            swap(tmp,st);
        }


        return ans;
    }
};
