class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        #define int long long
        unordered_set<int> s;
        set<int> temp;
        for (int i : nums) {
            set<int> r;
            r.insert(i);
            for (int j : temp) r.insert(i & j);
            temp = r;
            for (int j : temp) s.insert(j);
        }
        int ans = 1e18;
        for(auto it : s)
        {
            ans = min(ans,abs(k-it));
        }
            #undef int
        return ans;
    }
};
