class Solution {
public:
    #define ll long long
    int minimumDifference(vector<int>& a, int k) {
        ios_base::sync_with_stdio(false);
        cin.tie(0);

        int n = a.size();

        ll x = a[0] , ans = 2e9, v = 2e9;
        vector<ll> f(32, 0);
        int i = 0 , j =  0 ;

        while(1){
            if(v - k < 0 and i < j){
                if(i > -1){
                    for(ll k = 0 ; k < 32; k ++){
                        ll b = (a[i] >> k) & 1 ;
                        f[k] -= b ;
                    }
                }
                i ++ ;
            }else if(k - v == 0){
                return 0 ;
            }else{
                if(j >= n) break;
                for(ll k = 0 ; k < 32; k ++){
                    ll b = (a[j] >> k) & 1 ;
                    f[k] += b ;
                }
                j ++ ;
            }


            v = 0 ;
            for(ll k = 0 ; k < 32; k ++){
                if(f[k] == (j - i)){
                    v += (1ll << k);
                }
            }
            // cout << v << ' ' << i << ' ' << j << endl;;

            ans = min(ans, abs(k - v));
        }
        return (int)ans ;
    }
};
