#define ll long long

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<ll> bits(32, 1);
        ll currAnd = (1ll<<32) - 1;
        ll ans = INT_MAX;

        ll i=0,j=0;
        while(j < nums.size()) {
            currAnd &= nums[j];
            ll value = nums[j], ind = 0;
            while(value) {
                if(value & 1) bits[ind] ++;
                ind ++;
                value = (value>>1);
            }
            ans = min(ans, abs(currAnd - k));


            while(currAnd < k) {
                ll value = nums[i], ind = 0;
                while(value) {
                    if(value & 1) bits[ind] --;
                    ind ++;
                    value /= 2;
                }
                for(int bit=0;bit<31;bit++) {
                    if(currAnd & (1<<bit)) continue;
                    if(bits[bit] == (j-i+1)) currAnd |= (1<<bit);
                }
                i ++;
                ans = min(ans, abs(currAnd - k));
            }
            j ++;
        }
        return ans;
    }
};
