class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int minDiff = INT_MAX;
        unordered_set<int> currentANDs;

        for (int num : nums) {
            unordered_set<int> newANDs;
            newANDs.insert(num);
            for (int val : currentANDs) {
                newANDs.insert(val & num);
            }
            currentANDs = newANDs;

            for (int val : currentANDs) {
                minDiff = min(minDiff, abs(k - val));
            }
        }

        return minDiff;
    }
};
