class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        N = len(nums)

        @cache
        def go(idx, cur):
            if idx == N:
                return abs(cur - k)
            best = inf
            best = min(best, go(idx + 1, cur & nums[idx]))
            best = min(best, go(idx + 1, nums[idx]), abs(cur - k))

            return best

        return go(0, (1 << 30) - 1)
