class Solution {

	int Value(int S, int E, vector<vector<int> >& Count)
	{
		int i;
		int Result = 0;

		for(i = 0; i < 32; i++)
		{
			if((Count[(E + 1)][i] - Count[S][i]) == (E - S + 1))
			{
				Result += (0x1 << i);
			}
		}

		return Result;
	}

public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<vector<int> > Count(1, vector<int>(32));
		vector<int> Now(32);
		int N = nums.size();
		int i, j;
		int Result = 1000000000;

		for(i = 0; i < N; i++)
		{
			int P = 0;

			while(nums[i])
			{
				Now[P++] += (nums[i] & 0x1);
				nums[i] >>= 1;
			}

			Count.push_back(Now);
		}

		for(i = 0; i < N; i++)
		{
			int S = i;
			int E = N - 1;

			while(S < E)
			{
				int C = (S + E) / 2;

				if(Value(i, C, Count) <= k)
				{
					E = C;
				}
				else
				{
					S = C + 1;
				}
			}

			Result = min(Result, abs(Value(i, S, Count) - k));
			if(S != i)
			{
				Result = min(Result, abs(Value(i, (S - 1), Count) - k));
			}

			if(S != (N - 1))
			{
				Result = min(Result, abs(Value(i, (S + 1), Count) - k));
			}
		}

		return Result;
    }
};
