class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int nlen = nums.size();

        vector<unordered_set<int>> isVisited(nlen);
        int res = INT_MAX;
        for(int ni = 0; ni < nlen; ++ni){
            int cur = nums[ni];
            for(int nj = ni; nj < nlen; ++nj){
                if(cur == 0)break;
                else if(isVisited[nj].count(cur))break;
                //cout<<ni<<" nj"<<nj<<" cur"<<cur<<endl;
                isVisited[nj].insert(cur);
                cur &= nums[nj];
                res = min(res, abs(cur - k));
            }
        }
        return res;
    }
};
