msk = -1

class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        vis = [msk]
        ans = 10 ** 9
        for num in nums:
            nvis = [num]
            for x in vis:
                if nvis[-1] & x != nvis[-1]:
                    nvis.append(nvis[-1] & x)
            vis = nvis
            for x in vis:
                if abs(x - k) < ans:
                    ans = abs(x - k)
        return ans
