class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int m = nums.size();
        unordered_map<int, vector<int>> hm;     // 第i为0的数的下标有哪些
        for (int i = 0; i < 32; ++i) {
            for (int j = 0; j < m; ++j) {
                if ((1 << i) & nums[j]) {
                } else {
                    hm[i].push_back(j);
                }
            }
        }
        int i = 0, j = 0;
        int ands = -1, closet = INT_MAX;
        while (j < m) {
            ands &= nums[j];
            while (i <= j && ands < k) {  // 移动左指针, 计算i+1~j的
                closet = min(closet, abs(k - ands));
                int new_ands = 0;
                for (int k = 0; k < 32; ++k) {
                    vector<int>& v = hm[k];
                    auto it = lower_bound(v.begin(), v.end(), i + 1);
                    bool zero = false;
                    if (it != v.end()) {    // 这位是0
                        if (*it <= j) {
                            zero = true;
                        }
                    }
                    if (!zero) {
                        new_ands |= (1 << k);
                    }
                }
                ands = new_ands;
                ++i;
            }
            if (abs(ands - k) < closet) {
                closet = abs(ands - k);
            }
            ++j;
        }
        return closet;
    }
};
