let helper = (count, cur, tCount) => {
    let copy = [...count];
    for (let t = 0; t < 32; t++) {
            if ((cur >> t) & 1) {
                copy[31 - t] -= 1;
            }
        }
    let res = 0;
    for (let t = 0; t < 32; t++) {
        if (copy[31 - t] === tCount) {
            res |= (1 << t);
        }
    }
    return res;
}
