    let res = Math.abs(nums[0] - k);
    let i = 0;
    let j = 0;
    let val1 = nums[0];
    let val2 = nums[0];
    let count1 = nums[0].toString(2).padStart(32, '0').split('').map(Number);
    let count2 = nums[0].toString(2).padStart(32, '0').split('').map(Number);

    for (let w = 1; w < nums.length; w++) {
        let cur = nums[w];
        for (let t = 0; t < 32; t++) {
            if ((cur >> t) & 1) {
                count1[31 - t] += 1;
                count2[31 - t] += 1;
            }
        }
        val1 &= cur;
        val2 &= cur;
        while (i < w && val1 <= k) {
            val1 = helper2(count1, nums[i], w - i);
            i += 1;
        }
        while (j < w && helper(count2, nums[j], w - j) <= k) {
            val2 = helper2(count2, nums[j], w - j);
            j += 1;
        }
        // console.log(val1, val2);
        res = Math.min(res, Math.abs(val1 - k), Math.abs(val2 - k));
    }
