func minimumDifference(nums []int, k int) int {
    ans := abs(nums[0] - k)
    dp := make(map[int]bool)
    dp[nums[0]] = true
    for i, v := range nums {
        if i == 0 {
            continue
        } else {
            tmp := make(map[int]bool)
            for key, _ := range dp {
                tmp[key&v] = true
                ans = min(ans,abs(key&v-k))
            }
            tmp[v] = true
            ans = min(ans,abs(v-k))
            dp = tmp
        }
    }
    return ans
}

func abs(a int) int {
    if a > 0 {
        return a
    }
    return -a
}
