    #define INF INT32_MAX
class Solution {
public:

    class cls
    {
    public:
        vector<int> tree;
        int n;

        // Constructor
        cls(vector<int> &arr)
        {
            n = arr.size();
            tree.resize(4 * n);
            build(arr, 0, 0, n - 1);
        }

        // Function to build the Segment Tree
        void build(vector<int> &arr, int node, int start, int end)
        {
            if (start == end)
            {
                tree[node] = arr[start];
            }
            else
            {
                int mid = (start + end) / 2;
                build(arr, 2 * node + 1, start, mid);
                build(arr, 2 * node + 2, mid + 1, end);

                tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
            }
        }

        int fn2(int l, int r)
        {
            return query(0, 0, n - 1, l, r);
        }

        int query(int node, int start, int end, int l, int r)
        {
            if (r < start || end < l)
            {
                return INF;
            }
            if (l <= start && end <= r)
            {
                return tree[node];
            }
            int mid = (start + end) / 2;
            int p1 = query(2 * node + 1, start, mid, l, r);
            int p2 = query(2 * node + 2, mid + 1, end, l, r);

            return p1 & p2;
        }
    };

    int util(vector<int> arr, int k)
    {
        int n = arr.size();
        cls st(arr);
        int ans = INF;

        for (int i = 0; i < n; i++)
        {
            int l = i - 1, r = n - 1, mid;

            while (r - l > 1)
            {
                mid = (l + r) / 2;
                if (st.fn2(i, mid) >= k)
                    l = mid;
                else
                    r = mid;
            }
            if (l != i - 1)
            {
                ans = min(ans, abs(st.fn2(i, l) - k));
            }
            ans = min(ans, abs(k - st.fn2(i, r)));
        }
        return ans;
    }

    int minimumDifference(vector<int>& arr, int k) {
        int x = util(arr, k);
        return abs(x);
    }
};
