class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> v(n ,vector<int>(32 , 0));
        for(int i = 0 ; i < n ; ++i){
            for(int j = 0 ; j < 32 ; ++j){
                if(i != 0){
                    v[i][j] = v[i - 1][j];
                }
                if((nums[i] >> j) & 1){
                    v[i][j]++;
                }
            }
        }
        long long ans = LLONG_MAX;
        for(int i = 0 ; i < n ; ++i){
            if(k == nums[i]){
                return 0;
            }
            if(k > nums[i]){
                ans = min(ans , (long long)(k - nums[i]));
                continue;
            }
            int b = i + 1 , e = n - 1;
            int at = i;
            while(b <= e){
                int mid = b + (e - b) / 2;
                long long curV = 0;
                for(int j = 0 ; j < 32 ; ++j){
                    if((v[mid][j] - (i == 0 ? 0 : v[i - 1][j])) >= (mid - i + 1)){
                        curV |= ((long long)1 << (long long)j);
                    }
                }
                if(curV >= k){
                    at = mid;
                    b = mid + 1;
                }
                else{
                    e = mid - 1;
                }
            }
            long long curV = 0;
            for(int j = 0 ; j < 32 ; ++j){
                if((v[at][j] - (i == 0 ? 0 : v[i - 1][j])) >= (at - i + 1)){
                    curV |= (long long)(1 << j);
                }
            }
            ans = min(ans , curV - (long long)k);
            if(at != (n - 1)){
                ++at;
                curV = 0;
                for(int j = 0 ; j < 32 ; ++j){
                    if(v[at][j] - (i == 0 ? 0 : v[i - 1][j]) >= (at - i + 1)){
                        curV |= ((long long)1 << (long long)j);
                    }
                }
                ans = min(ans , k - curV);
            }
        }
        return ans;
    }
};
