class Solution {
public:
    void add(int x,map<int,int> &mp){
        for(int i=0;i<32;i++){
            if((x>>i)&1) mp[i]++;
        }

    }
    void del(int x,map<int,int> &mp){
        for(int i=0;i<32;i++){
            if((x>>i)&1) mp[i]--;
        }

    }

    int minimumDifference(vector<int>& nums, int k) {
        int f=0;
        int b=0;
        int n=nums.size();
        int mn=INT_MAX;
        map<int,int> mp;
        while(f<n){
            if(nums[f]<=k){
                mn=min(k-nums[f],mn);
                f++;
                mp.clear();
                b=f;
                continue;
            }
            add(nums[f],mp);
            int a=0;
            int sz=(f-b+1);
            for(int i=0;i<32;i++){
                if(mp[i]==sz){
                    a=((1<<i)|a);
                }

            }
            if(a<k){
                mn=min(k-a,mn);
                del(nums[b],mp);
                del(nums[f],mp);
                b++;

            }
            else{
                f++;
                mn=min(a-k,mn);

            }



        }
        return mn;

    }
};
