class Solution {
public:
    typedef long long ll;
    int minimumDifference(vector<int>& nums, int k) {
        ll n=nums.size();
      vector<vector<ll>>dp(n+1,vector<ll>(32,0LL));
        for(ll i=0;i<32;i++){
            ll ew=(1LL<<i);
            for(ll j=1;j<=n;j++){
                 ll x=0;
                if((ew&nums[j-1])){
                    x=1;
                }
                dp[j][i]=dp[j-1][i]+x;

            }
        }
        ll ans=LLONG_MAX;
        for(ll i=0;i<n;i++){
            ll lo=i,hi=n-1;
            while(lo<hi){
                ll mid=(lo+hi)/2;
                ll nd=0;
                ll len=mid-i+1;
                for(int j=0;j<32;j++){
                    ll jk=dp[mid+1][j]-dp[i][j];
                    if(jk==len){
                        nd+=(1LL<<j);
                    }
                }
                if(nd>k){
                   lo=mid+1;
                }
                else{
                  hi=mid;
                }


            }


              ll ln=lo-i+1,no=0;
                for(int j=0;j<32;j++){
                    ll jk=dp[lo+1][j]-dp[i][j];
                    if(jk==ln){
                        no+=(1LL<<j);
                    }
                }
            ans=min(ans,abs(k-no));




             lo=i,hi=n-1;
            while(lo<hi){
                ll mid=(lo+hi)/2;
                if(hi-lo==1){
                  mid=hi;
                }
                ll nd=0;
                ll len=mid-i+1;
                for(int j=0;j<32;j++){
                    ll jk=dp[mid+1][j]-dp[i][j];
                    if(jk==len){
                        nd+=(1LL<<j);
                    }
                }
                if(nd<k){
                   hi=mid-1;
                }
                else{
                  lo=mid;
                }


            }


              ll len=lo-i+1;
                no=0;
                for(int j=0;j<32;j++){
                    ll jk=dp[lo+1][j]-dp[i][j];
                    if(jk==len){
                        no+=(1LL<<j);
                    }
                }
            ans=min(ans,abs(k-no));





        }

        return ans;

    }
};
