class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        ans = float("inf")
        nums = [k for k, _ in groupby(nums)]
        while len(nums) > 1:
            ans = min(ans, min(abs(k - num) for num in nums))
            nums = [
                k for k, _ in groupby(
                    n1 & n2 for n1, n2 in pairwise(nums)
                )
            ]
        ans = min(ans, abs(k - nums[0]))
        return ans
