class Solution {
    public int minimumDifference(int[] arr, int target) {
        int[][] matrix = new int[arr.length][31];
        for(int i=0;i<arr.length;i++){
            for(int j=0;j<31;j++){
                if(i==0){
                    if(((arr[i]>>j)&1) ==1){
                        matrix[i][j]=1;
                    }
                    else matrix[i][j]=0;
                }
                else{
                    if(((arr[i]>>j)&1) ==1){
                        matrix[i][j]=1+matrix[i-1][j];
                    }
                    else matrix[i][j]=0+matrix[i-1][j];
                }
            }
        }
        // for(int i=0;i<arr.length;i++){
        //     for(int j=0;j<17;j++) System.out.print(matrix[i][j]+" ");
        //     System.out.println();
        // }
        int ans =Integer.MAX_VALUE;
        for(int i=0;i<arr.length-1;i++){
            int ll = i+1;
            int ul = arr.length-1;
            int ind =-1;
            while(ll<=ul){
                int mid = ll+(ul-ll)/2;
                int aa = func(i,mid,matrix);
                //  System.out.println(aa);
                // System.out.println(i+" "+func(i,mid,matrix));
                if(aa>=target){
                    ind=mid;
                    ll=mid+1;
                }
                else ul = mid-1;
            }
        //    if(ind!=-1) System.out.println(arr[i]+" "+func(i,ind,matrix));
        // System.out.println(i+" "+ind);
            if(ind!=-1){
                ans=Math.min(ans,func(i,ind,matrix)-target);
            }


             ll = i+1;
             ul = arr.length-1;
             ind =-1;
            while(ll<=ul){
                int mid = ll+(ul-ll)/2;
                int aa = func(i,mid,matrix);
                // if(i==0) System.out.println(aa+" "+mid);
                // System.out.println(i+" "+func(i,mid,matrix));
                // System.out.println(aa);
                if(aa<=target){
                    ind=mid;
                    ul=mid-1;
                }
                else ll = mid+1;
            }
        //    if(ind!=-1) System.out.println(arr[i]+" "+func(i,ind,matrix));
        // if(i==0) System.out.println(i+" "+ind);
            if(ind!=-1){
                ans=Math.min(ans,target-func(i,ind,matrix));
            }

        }
        for(int i=0;i<arr.length;i++){
            ans=Math.min(ans,Math.abs(arr[i]-target));
        }
        return ans;
    }
    static int func(int l , int r,int[][] matrix){
        int ans =0;
        for(int i=0;i<31;i++){
            if(l==0){
                if((matrix[r][i])==(r-l+1)) {
                ans+=(1<<i);
                // System.out.println("Yes"+" "+ans);

            }
            }
            else{
            if((matrix[r][i]-matrix[l-1][i])==(r-l+1)) {
                ans+=(1<<i);
                // System.out.println("Yes"+" "+ans);

            }
            }
        }
        return ans;
    }
}
