class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        set<int> st, nst;
        int ans = 1e9;
        for(auto &i : nums){
            nst.insert(i);
            ans = min(ans, abs(k-i));
            for(auto &j : st){
                // cout<<(i&j)<<" ";
                nst.insert(i&j);
                ans = min(ans, abs(k-(i&j)));
            }
            // cout<<endl;
            st = nst;
            nst.clear();
        }
        return ans;
    }
};
