class Solution {
public:
    int getAnd(int l,int r,vector<vector<int>>&prefix)
    {
            int dist=r-l+1,curr_and=0;
            for(int bit=0;bit<32;bit++)
            {
                if(l>0)
                {
                    if((prefix[r][bit]-prefix[l-1][bit])==dist)curr_and^=((1LL<<bit));
                }
                else
                {
                    if(prefix[r][bit]==dist)curr_and^=((1LL<<bit));
                }
            }
            return curr_and;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>prefix(n,vector<int>(32,0));
        for(int i=0;i<n;i++)
        {
            if(i>0)for(int bit=0;bit<32;bit++)prefix[i]=prefix[i-1];
            for(int bit=0;bit<32;bit++)
            {
                if((1LL<<bit)&nums[i])prefix[i][bit]++;
            }
        }
        int diff=1e9;
        //bitwise_and-k;
        for(int i=0;i<n;i++)
        {
        int lo=i,hi=n-1;
        while(lo<=hi)
        {
            int mid=(lo+hi)>>1;
            int curr_and=getAnd(i,mid,prefix);
            if(curr_and>=k)
            {
                diff=min(diff,curr_and-k);
                lo=mid+1;
            }
            else hi=mid-1;
        }
        }
        //k-bitwise_and;
        for(int i=0;i<n;i++)
        {
        int lo=i,hi=n-1;
        while(lo<=hi)
        {
            int mid=(lo+hi)>>1;
            int curr_and=getAnd(i,mid,prefix);
            if(curr_and<=k)
            {
                diff=min(diff,k-curr_and);
                hi=mid-1;
            }
            else lo=mid+1;
        }
        }
        return diff;
    }
};
