vector<int>t;
int n;
void build(vector<int>& a, int v, int tl, int tr) {
    if (tl == tr) {
        t[v] = a[tl];
    } else {
        int tm = (tl + tr) / 2;
        build(a, v*2, tl, tm);
        build(a, v*2+1, tm+1, tr);
        t[v] = t[v*2] & t[v*2+1];
    }
}
int sum(int v, int tl, int tr, int l, int r) {
    if (l > r) 
        return INT_MAX;
    if (l == tl && r == tr) {
        return t[v];
    }
    int tm = (tl + tr) / 2;
    return sum(v*2, tl, tm, l, min(r, tm))
           & sum(v*2+1, tm+1, tr, max(l, tm+1), r);
}
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        n = nums.size();
        t.assign(4*n,0);
        build(nums,1,0,n-1);
        int ans = INT_MAX;
        for(int i=0;i<n;++i)
        {
            int l = i-1,r = n-1;
            while(l+1 < r)
            {
                int mid = (l+r)/2;
                int j = sum(1,0,n-1,i,mid);
                if(j >=k)
                {
                    l = mid;
                }
                else r = mid;
            }
            if(l+1 != i)
            {
                ans = min(ans,abs(sum(1,0,n-1,i,l)-k));
            }
            ans = min(ans,abs(sum(1,0,n-1,i,r)-k));
        }
        return ans;
    }
};
